function isValidDate(dateString) {
    var ExDate= /^\d{4}-\d{2}-\d{2}$/;
    if(!dateString.match(ExDate)) return false;  // Invalid format
    var d = new Date(dateString);
    var dNum = d.getTime();
    if(!dNum && dNum !== 0) return false; // NaN value, Invalid date
    return d.toISOString().slice(0,10) === dateString;
}


$(document).ready(function () {
    var show=1;
    var show=0;
    var date= $("#reservationDate").value;

    $('#disp').click(function(){
        if (!$("#reservationDate").val() || isValidDate(date) ){
            alert("seleziona una data primadi continuare");
        }

        else if (show==0){
            $('#disp-panel').show();
            show=1;
            console.log(show);
        }
        else{

            $('#disp-panel').hide();
            show=0;
      }  
    });
});
