$(document).ready(function () {
    var show=1;
    var show=0;
    var date= document.getElementById("reservationDate").value;
    var data_valid = /^[0-9]{1,2}-[0-9]{1,2}-[0-9]{4}+$/;



    $('#disp').click(function(){
        if (!$("#reservationDate").val() || (!data_valid.test(date)) ){
            alert("seleziona una data primadi continuare");
        }

        else if (show==0){
            $('#disp-panel').show();
            show=1;
            console.log(show);
        }
        else{

            $('#disp-panel').hide();
            show=0;
      }  
    });
});
