$(document).ready(function () {
    var show=1;
    var show=0;

    $('#disp').click(function(){
        if ($("reservationDate").value==""){
            alert("seleziona una data primadi continuare");
        }

        else if (show==0){
            $('#disp-panel').show();
            show=1;
            console.log(show);
        }
        else{

            $('#disp-panel').hide();
            show=0;
      }  
    });
});
