$(document).ready(function () {

    var show=0;

    var data_valid =/^(\d{1,2})\/(\d{1,2})\/(\d{4})$/;

$('#disp').click(function(){
        var date= document.getElementById("reservationDate").value;
        var dataselezionata= document.getElementById("pickedDate");
        dataselezionata.value=date;
        console.log(date);
       console.log(dataselezionata);
        if(show==0){

            if (!$("#reservationDate").val() || (!data_valid.test(date)) ){
            alert("Seleziona una data prima di continuare");
            }

            else{
            $('#disp-panel').show();
            show=1;

            }

        }  

        else{
            $('#disp-panel').hide();
            show=0;
      }  
    });
});
